for (int i = 0; i < puzzleInput.Length; i++)
{
    var position = i + 4;
    var range = new Range(new Index(i), new Index(position));
    if (puzzleInput.Take(range).Distinct().Count() == 4)
    {
        Console.WriteLine(position);
        break;
    }
}

